aws cloudwatch put-metric-data --namespace Windows/Memory --dimension InstanceId="$((Invoke-WebRequest -Uri http://169.254.169.254/latest/meta-data/instance-id).Content)" --unit Percent --value "$(Get-WmiObject win32_operatingsystem -ComputerName $env:computername | Foreach {"{0:N0}" -f ((($_.TotalVisibleMemorySize - $_.FreePhysicalMemory)*100)/ $_.TotalVisibleMemorySize)})" --metric-name "MemoryUtilization"
